package model.v1

import org.insightedge.scala.annotation._
import org.openspaces.spatial.shapes._

import scala.beans.{BeanProperty, BooleanBeanProperty}

case class School(

                    @BeanProperty
                    @SpaceId(autoGenerate = true)
                    var id: String,

                    @BeanProperty
                    var name: String,

                    @BeanProperty
                    @SpaceIndex
                    var category: String,

                    @BeanProperty
                    var city: String,

                    @BeanProperty
                    var address: String,

                    @BeanProperty
                    var phone: String,

                    @BeanProperty
                    @SpaceSpatialIndex
                    var location: Point

                  ) {

  def this() = this(null, null, null, null, null, null, null)

}
